package jjjjj0001;

public class pyramid_0_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int k;
		for(int i=1;i<=5;i++)
		{
			for(int j=1;j<=i;j++)
			{
				k=i+j;
				if(k%2==1)
					System.out.print(" "+0);
				else
					System.out.print(" "+1);
			}
			System.out.println();
		}
		
		
	}

}
